    @Override
    public List<TInterprise> getInterpriseList() {
        TInterpriseExample example = new TInterpriseExample();
        List<TInterprise> interpriseList = interpriseMapper.selectByExampleWithBLOBs(example);

        for (int i = 0; i < interpriseList.size(); i++) {

            TInterprise interprise =  interpriseList.get(i);
            String samePerson = interprise.getSamePerson();

            String peid = interprise.getEid();
            String pename = interprise.getEname();
            Short tag = interprise.getTag();
            String createdTime = interprise.getCreatedTime();

            System.out.println(i);
            System.out.println(peid);

            //if("6f8711fe-052a-45b4-969c-4cae90c71ec1".equals(peid) || "bc1b6170-c80e-4909-bfda-37e6da3255f0".equals(peid) || "c1202cab-9548-40ff-8c7b-6225ecb28825".equals(peid)){
                InterpriseResultPojo interpriseResultPojo = JsonUtils.jsonToPojo(samePerson, InterpriseResultPojo.class);
                List<InterpriseResult> interpriseResultList = interpriseResultPojo.getEnterpise();

                for (int j = 0; j < interpriseResultList.size(); j++) {
                    InterpriseResult interpriseResult =  interpriseResultList.get(j);

                    String ceid = interpriseResult.getEid();
                    String cename = interpriseResult.getEname();
                    List<InterpriseRelation> relationList = interpriseResult.getRelated_by();

                    for (int k = 0; k < relationList.size(); k++) {
                        InterpriseRelation interpriseRelation =  relationList.get(k);
                        String content = interpriseRelation.getContent();
                        String type = interpriseRelation.getType();

                        TInterpriseCtrl interpriseCtrl = new TInterpriseCtrl();
                        interpriseCtrl.setIds(IDUtils.getGeneId());
                        interpriseCtrl.setPeid(peid);
                        interpriseCtrl.setPname(pename);
                        interpriseCtrl.setTag(tag);
                        interpriseCtrl.setCreatedTime(createdTime);

                        interpriseCtrl.setCeid(ceid);
                        interpriseCtrl.setCname(cename);
                        interpriseCtrl.setTypes(type);
                        interpriseCtrl.setContent(content);

                        interpriseCtrl.setCreated(new Date());
                        interpriseCtrl.setUpdated(new Date());

                        insertInterpriseCtrl(i,interpriseCtrl);
                    }
                }
            }

        //}

        return null;
    }

